import sqlite3

# Conectar a la base de datos (si no existe, se creará)
conexion = sqlite3.connect('database_Carreras.db')

# Crear un cursor para ejecutar consultas SQL
cursor = conexion.cursor()

# Crear una tabla para almacenar las carreras
cursor.execute('''CREATE TABLE IF NOT EXISTS carreras (
                    id INTEGER PRIMARY KEY,
                    Carreras TEXT,
                    Descripcion TEXT
                 )''')

